const normalizeId = (mapId?: string | null): string | null => {
  if (!mapId) return null;
  return mapId.trim().toUpperCase();
};

export const getMapMetadata = (mapId?: string | null): MapMetadata | null => {
  const key = normalizeId(mapId);
  if (!key) return null;
  return MAP_METADATA[key] || null;
};

export const getMapName = (mapId?: string | null): string => {
  const meta = getMapMetadata(mapId);
  if (!meta) {
    if (!mapId) return 'Unknown Map';

    const cleaned = mapId
      .replace(/^.+?_/, '')
      .replace(/_/g, ' ')
      .replace(/\s+/g, ' ')
      .trim()
      .toLowerCase();

    if (!cleaned) return 'Unknown Map';

    return cleaned.replace(/\b([a-z])/g, (_, letter) => letter.toUpperCase());
  }
  return (meta.name || meta.fallbackName || 'Unknown Map').trim();
};

export const getMapImage = (mapId?: string | null): string | undefined => {
  return getMapMetadata(mapId)?.imagePath;
};

export const mapMetadata = MAP_METADATA;
